def min_extra_tiles_to_win(tiles):
    # Parse the tiles into (number, suit) tuples
    parsed = [(int(t[0]), t[1]) for t in tiles]

    # Check if already have a koutsu (three identical tiles)
    from collections import Counter
    counter = Counter(tiles)
    for tile, count in counter.items():
        if count >= 3:
            return 0

    # Check if already have a shuntsu (sequence of three numbers in the same suit)
    # For that group the tiles by suit
    suits = {'m': [], 'p': [], 's': []}
    for n, s in parsed:
        suits[s].append(n)

    # For each suit, check if there are three consecutive numbers
    for s, nums in suits.items():
        nums = sorted(nums)
        # Check for a sequence of 3 consecutive
        for i in range(len(nums)):
            for j in range(i+1, len(nums)):
                for k in range(j+1, len(nums)):
                    if nums[k] - nums[j] == 1 and nums[j] - nums[i] == 1:
                        return 0

    # Since no koutsu or shuntsu with current tiles,
    # we need to find the minimum extra tiles needed to form either a koutsu or shuntsu

    # Helper function to check if we can form a koutsu or shuntsu from the current tiles and adding x extra tiles
    # but since the hand is very small, we can try just brute force all possible ways with at most 3 additions
    def can_form_mentsu(tiles, extra_tiles):
        combined = tiles + extra_tiles
        combined_counter = Counter(combined)
        for tile, count in combined_counter.items():
            if count >= 3:
                return True
        # Check shuntsu
        suits = {'m': [], 'p': [], 's': []}
        for t in combined:
            n = int(t[0])
            s = t[1]
            suits[s].append(n)
        for s, nums in suits.items():
            nums = sorted(nums)
            nums_set = set(nums)
            for n in nums:
                if n+1 in nums_set and n+2 in nums_set:
                    return True
        return False

    # Try adding 0 to 3 extra tiles (since we only have 3 tiles, max needed is 3)
    for add in range(4):
        # Generate all possible combinations of add tiles
        # Tiles are from 1 to 9 and suits m, p, s
        from itertools import product
        all_tiles = [str(n) + s for n in range(1, 10) for s in 'mps']
        if add == 0:
            if can_form_mentsu(tiles, []):
                return 0
            else:
                continue
        else:
            # We only need to find one combination that forms mentsu
            for extra_tiles in product(all_tiles, repeat=add):
                if can_form_mentsu(tiles, list(extra_tiles)):
                    return add

    return 3


# Read input
tiles = input().split()

# Compute and print the result
print(min_extra_tiles_to_win(tiles))

# The output is the minimum number of extra tiles needed

